const budget=Object.freeze([{value:250,description:"Sold old TV 📺",user:"jonas"},{value:-45,description:"Groceries 🥑",user:"jonas"},{value:3500,description:"Monthly salary 👩‍💻",user:"jonas"},{value:300,description:"Freelancing 👩‍💻",user:"jonas"},{value:-1100,description:"New iPhone 📱",user:"jonas"},{value:-20,description:"Candy 🍭",user:"matilda"},{value:-125,description:"Toys 🚂",user:"matilda"},{value:-1800,description:"New Laptop 💻",user:"jonas"}]),spendingLimits=Object.freeze({jonas:1500,matilda:100}),getLimit=(e,i)=>e?.[i]??0,addExpense=function(e,i,n,s,t="jonas"){t=t.toLowerCase();return n<=getLimit(i,t)?[...e,{value:-n,description:s,user:t}]:e},newBudget1=addExpense(budget,spendingLimits,10,"Pizza 🍕"),newBudget2=addExpense(newBudget1,spendingLimits,100,"Going to movies 🍿","Matilda"),newBudget3=addExpense(newBudget2,spendingLimits,200,"Stuff","Jay"),checkExpenses=(e,i)=>e.map(e=>e.value<-getLimit(i,e.user)?{...e,flag:"limit"}:e),finalBudget=checkExpenses(newBudget3,spendingLimits),logBigExpenses=(console.log(finalBudget),function(e,i){e=e.filter(e=>e.value<=-i).map(e=>e.description.slice(-2)).join(" / ");console.log(e)});logBigExpenses(finalBudget,500);